function [pName, pValue, pDescr]   = report_info(obj)

% Documentation: class_pset_plotter_space_psd.txt
% Description: report.reportable interface

pName = {...
    'BlackBgPlots', ...
    'SVG', ...  
    'PrintDrivers', ...
    'Folder', ... 
    'Visible' ...
    };
    
pDescr = {...
    'Generate also plots with a black background?', ...    
    'Generate .svg figures?', ...    
    'Additional printing drivers. Driver png is always used.', ...
    'Directory where the generated figures should be stored', ...
    'Should the figures be visible?' ...
    };

pValue = cellfun(@(x) obj.(x), pName, 'UniformOutput', false);
