function x = force_builtin(x)

import goo.force_builtin;

builtinClasses = { ...
    'single', ...
    'double', ...
    'int8', ...
    'int16', ...
    'int32', ...
    'int64', ...
    'uint8', ...
    'uint16', ...
    'uint32', ...
    'uint64', ...
    'cell' , ...
    'struct', ...
    'logical', ...
    'char', ...
    'function_handle', ...
    };

if iscell(x),
    for i = 1:numel(x)
       x{i} = force_builtin(x{i}); 
    end
elseif isstruct(x),
    fNames = fieldnames(x);
    for i = 1:numel(fNames)
        x.(fNames{i}) = force_builtin(x.(fNames{i}));
    end
elseif ~ismember(class(x), builtinClasses),
    warning('off', 'MATLAB:structOnObject');
   
    x = force_builtin(struct(x));
    
    warning('off', 'MATLAB:structOnObject');

    
end


end