function obj = zscore(varargin)

import meegpipe.node.*;

myFilter = filter.function_handle(...
    'Operator', @(data) zscore(data), ...
    'Dim',      'cols'); % we want to apply Operator on each column

obj = bad_channels.criterion.var.new(...
    'Filter',       myFilter, ...
    'Min',          0, ...
    'Max',          4, ...
    'MaxCard',      Inf, ...
    'MinCard',      0, ...
    'Normalize',    false, ... 
    'LogScale',     false, ...
    'NN',           Inf, ...
    varargin{:});
    


end