function [pName, pValue, pDescr]   = report_info(obj)

% Documentation: class_pset_plotter_space_psd.txt
% Description: report.reportable interface

pName = {...
    'Estimator', ...
    'Channels', ...  
    'Windows', ...
    'MaxChannels', ... 
    'ChannelClass', ...
    'ChannelType', ...
    'MaxWindows', ...
    'WinLength', ...
    'Folder' ...
    'Plotter', ...
    'Args', ...
    };
    
pDescr = {...
    'Power Spectral Density estimator', ...    
    'Set(s) of channels to use for plotting', ...    
    'Data windows to consider, in samples', ...
    'Maximum number of channels to consider for plotting', ...
    'Plot only channels of this class(es)', ...
    'Plot only channels of this type(s)', ...
    'Maximum number of data windows to consider', ...
    'Analysis window length, in seconds', ...
    'Save the generated figures to this folder', ...  
    'Actual plots are generated by calling method plot() on this object', ...
    'Additional arguments for method psd() of the PDS estimator', ...   
    };

pValue = cellfun(@(x) obj.(x), pName, 'UniformOutput', false);
