function [pName, pValue, pDescr]   = report_info(obj)

% Documentation: class_pset_plotter_time_snapshots.txt
% Description: report.reportable interface

pName = {... 
    'BlackBgPlots', ...
    'PrintDrivers', ...
    'WinLength', ...
    'NbGoodEpochs', ...
    'NbBadEpochs', ...
    'Epochs', ...
    'Channels', ...
    'MaxChannels', ...
    'ChannelClass', ...
    'ChannelType', ...
    'ScaleFactor' ...
    'Folder', ...
    'Plotter' ...
    };
    
pDescr = {... 
    'Generate also figures on a black background', ...
    'Printing drivers', ...
    'Duration of the snapshots (seconds)', ...
    'Number of good epochs to plot', ...
    'Number of bad epochs to plot', ...
    'Boundaries of epochs to be plotted (samples)', ...
    'Channel sets to be plotted (indices)', ...
    'Maximum number of channels per snapshot', ...
    'Plot only channels of this class(es)', ...
    'Plot only channels of this type(s)', ...
    'Multiply default scale by this factor' ...
    'Directory where the generated figures will be stored', ...
    'Figures will be generated by calling method plot() on this object' ...
    };

pValue = cellfun(@(x) obj.(x), pName, 'UniformOutput', false);
