function add_event_gui(obj)

import physioset.event.utils.pop_eeglabel;
import physioset.event.utils.event_generator;

EEG = eeglab(obj, 'MemoryMapped', true);

% Create a generic event generator object that will send messages to the 
% provided physioset object obj
evGenObj = event_generator(obj);

% Add a listener to events generated by the event generator
add_event_listener(obj, evGenObj, 'AddEventGui');

pop_eeglabel(EEG, 'add', evGenObj); 



end